<?php define("TO_ROOT", "../../");set_time_limit(0);ini_set("memory_limit","1024M");header("Content-Type: application/json");header("Access-Control-Allow-Origin: *");header("Access-Control-Allow-Headers: Origin, X-Requested-With, Content-Type, Accept");if($_SERVER['REQUEST_METHOD'] == 'POST' && empty($_POST)) $_POST = json_decode(file_get_contents('php://input'), true);require_once TO_ROOT . "system/core.php";$data = HCStudio\Util::getVarFromPGS(); #actionif(!ob_start("ob_gzhandler")){	ob_start();	$data['gzip'] = false;} else $data['gzip'] = true;if($data['password']){	$_POST['password'] = sha1($data['password']);	$_GET['password'] = sha1($data['password']);}$UserLogin = new Welcome\UserLogin;if($UserLogin->_loaded){		$data['html'] = getHtml($UserLogin->mail);	$data['s'] = 1;	$data['r'] = 'DATA_OK';		} else {	$data['s'] = 0;	$data['r'] = 'NOT_LOADED';}function getHtml($email = false){	if($email)	{		return '<h2>Se han ingresado sus datos con éxito con el correo '.$email.'</h2>';	} else {		return '<h2>Se han ingresado sus datos con éxito</h2>';	}}echo json_encode($data); ?>